import{_ as e,c as a,a2 as r,o as n}from"./chunks/framework.DPuwY6B9.js";const s="/time%20cave.png",o="/gauntlet.png",i="/branch%20and%20bottleneck.png",c="/quest.png",d="/open%20map.png",l="/loop%20and%20grow.png",h="/confirmationChoice.png",m="/trackSwitchingChoice.png",g="/conversationNode.png",p="/chapterOneSortingHat.png",b="/endgameTimeCave.png",u="/enEmbranchmentsDark.png",f="/enEmbranchmentsLight.png",N=JSON.parse('{"title":"Branching Narratives","description":"","frontmatter":{"layout":"doc","title":"Branching Narratives","lang":"en-US"},"headers":[],"relativePath":"Notes/Branching narratives.md","filePath":"Notes/Branching narratives.md"}'),y={name:"Notes/Branching narratives.md"};function v(_,t,k,w,C,q){return n(),a("div",null,t[0]||(t[0]=[r('<h1 id="branching-narratives" tabindex="-1">Branching narratives <a class="header-anchor" href="#branching-narratives" aria-label="Permalink to &quot;Branching narratives&quot;">‚Äã</a></h1><div class="tip custom-block"><p class="custom-block-title">Definition</p><p>Narratives that present alternative story developments that are generally incompatible with each other. They affect <span class="highlight">major story and characters elements</span>.</p></div><h2 id="classic-structures-in-choice-based-games" tabindex="-1">Classic structures in choice-based games <a class="header-anchor" href="#classic-structures-in-choice-based-games" aria-label="Permalink to &quot;Classic structures in choice-based games&quot;">‚Äã</a></h2><p>From <a href="https://heterogenoustasks.wordpress.com/2015/01/26/standard-patterns-in-choice-based-games/" target="_blank" rel="noreferrer">Sam Kabo&#39;s Ashwell all-time great</a>:</p><table tabindex="0"><thead><tr><th>Structure</th><th>Representation</th><th>Notes</th></tr></thead><tbody><tr><td><strong>Time cave</strong></td><td><img src="'+s+'" alt="Time cave"></td><td>No variables, qualities, or stats</td></tr><tr><td><strong>Gauntlet</strong></td><td><img src="'+o+'" alt="Gauntlet"></td><td>Can be friendly or deadly<br> Low use of variables</td></tr><tr><td><strong>Branch and bottleneck</strong></td><td><img src="'+i+'" alt="Branch and bottleneck"></td><td>High use of variables <br> Often used to represent the passage of time and the world&#39;s and the player&#39;s growth <br> Works better in big pieces</td></tr><tr><td><strong>Quest</strong></td><td><img src="'+c+'" alt="Quest"></td><td>High use of variables <br> Works better in big pieces <br> Centered on exploration and environment <br> Narrative is fragmentary</td></tr><tr><td><strong>Open map</strong></td><td><img src="'+d+'" alt="Open map"></td><td>Centered on exploration and environment <br>High use of variables <br> Narrative is either fragmentary or loose-paced</td></tr><tr><td><strong>Sorting hat</strong></td><td></td><td>High usage of variables at start, and then not at all <br> Lots of work: custom systems + branch-exclusive content</td></tr><tr><td><strong>Floating modules</strong></td><td></td><td>Akin to storylets, same requirements of wide content and explicit (?) stats <br> Grinding stats can be incorporated into the game&#39;s design</td></tr><tr><td><strong>Loop and grow</strong></td><td><img src="'+l+'" alt="Loop and grow"></td><td>Notion of <em><strong>runs</strong></em> <br> High use of variables <br> Justified by the narrative <br> Often backed by the theme of escaping some kind of imprisonment <br> Can be complified by using main branches</td></tr></tbody></table><h2 id="small-scale-patterns-in-choice-based-games" tabindex="-1">Small-scale patterns in choice-based games <a class="header-anchor" href="#small-scale-patterns-in-choice-based-games" aria-label="Permalink to &quot;Small-scale patterns in choice-based games&quot;">‚Äã</a></h2><table tabindex="0"><thead><tr><th>Structure</th><th>Representation</th><th>Function</th><th>Further opportunities</th></tr></thead><tbody><tr><td>Confirmation-required choice</td><td><img src="'+h+'" alt="Confirmation choice"></td><td>Offer several occasions to drop out of a dangerous / stupid decision</td><td>Measure the player&#39;s proneness to risk</td></tr><tr><td>Track switching choice</td><td><img src="'+m+'" alt="Track switching choice"></td><td>Emulates indecision</td><td>Measure commitment, proneness to changing their mind</td></tr><tr><td>Scored choice</td><td></td><td>Aggregation of same-importance choices <br></td><td>Decompose a big decision into many smaller ones to make a tendency emerge</td></tr><tr><td>Re-enterable conversation node</td><td><img src="'+g+'" alt="Conversation nodes"></td><td><span class="highlight">Liven-up an exposition dump</span></td><td>Track what they seem to be most spontaneously interested in <br> Gate options behind variables</td></tr><tr><td>Chapter one sorting hat</td><td><img src="'+p+'" alt="Chapter one sorting hat"></td><td>Offers early game replayability</td><td>/</td></tr><tr><td>Endgame time cave</td><td><img src="'+b+'" alt="Endgame time cave"></td><td>Pay-off setup <br> Seems most adapted to disjointed consequences, for global consequences use <a href="https://emshort.blog/2019/11/23/narrative-states/" target="_blank" rel="noreferrer">narrative states</a></td><td>/</td></tr></tbody></table><h2 id="embranchments-don-t-matter" tabindex="-1">Embranchments don&#39;t matter <a class="header-anchor" href="#embranchments-don-t-matter" aria-label="Permalink to &quot;Embranchments don&#39;t matter&quot;">‚Äã</a></h2><p><a href="https://www.youtube.com/embed/TEa9aSDHawA" target="_blank" rel="noreferrer">Choices don&#39;t have to be impactful, they just have to feel that way</a>. What matters most is having a <span class="highlight">compelling story</span>.</p><p>Players rarely replay their games, many branchs are not seen despite their number or quality.</p><div class="tip custom-block"><p class="custom-block-title">Characteristics of a good main embranchment</p><ul><li>Produces changes in scenes or character&#39;s behaviors</li><li>Allows unique ways of reaching one&#39;s goals</li><li>Marks a turning point that may be remembered and reffered to later</li><li>Doesn&#39;t produce disjointed narratives, only affects minor secondary elements (that can still be displayed in small scenes)</li></ul></div><h3 id="how-to-build-an-embranchment" tabindex="-1">How to build an embranchment <a class="header-anchor" href="#how-to-build-an-embranchment" aria-label="Permalink to &quot;How to build an embranchment&quot;">‚Äã</a></h3><ul><li>Can stem from the order of scenes in the determined narrative, or the order the player learns key info in</li><li>Can stem from major conflicts ands character&#39;s goals</li><li>Good practice: first embranchment asap, then on a regular rythm (25/30min according to Episode Interactive&#39;s own Cass Phillipps)</li></ul><div class="info custom-block"><p class="custom-block-title">ex: Branching Ulysse&#39;s return to Ithaca</p><p><img src="'+u+'" alt="Ulysses" class="dark-only"><img src="'+f+'" alt="Ulysses" class="light-only"> ‚ùóNot to be read as a geographical map, each segment can only be played once; to make the most of your content, you&#39;d probably want your player to play every single segment once before getting to Ithaca. <br> üí° Scoring choices at the end can be efficiently used in conjunction with <a href="https://emshort.blog/2019/11/23/narrative-states/" target="_blank" rel="noreferrer">narrative states</a></p></div>',14)]))}const S=e(y,[["render",v]]);export{N as __pageData,S as default};
